mapper.select:
select id, password, name, age, phone_number, gender
from user
where id = #{id} and password = #{password}

select  id, password, name, age, phone_number, gender
from user
where id = #{id}

select token
from user
where id = #{id}

mapper.update:
update user
set token = #{token}
where id = #{id}

update user
set token = #{token, jdbcType=VARCHAR}
where id = #{id}
